<script>
export default {
   name: 'DivTimerPaper',
   props: {
      type: {
         type: String,
         required: true,
      },
      timer: {
         type: String,
         default: '00:00:00',
      },
   },

   data() {
      return {};
   },

   computed: {
      classes() {
         return {
            'post--study': this.type == 'study',
            'post--rest': this.type == 'rest',
         };
      },
      label() {
         if (this.type == 'study') return '공부중';
         else return '휴식중';
      },
   },
};
</script>
<style scoped>
.postit {
   width: 15vw;
   min-width: 140px;
   max-width: 160px;
}

.postit .content {
   position: absolute;
   bottom: 30%;
   left: 50%;
   transform: translateX(-50%) rotate(-4deg);
   width: 100%;
   text-align: center;
   font-size: 1.5vmin;
   font-weight: 700;
   color: white;
}

/* 바인딩 된 속성에 따라 포스트잇 색상을 변경 */
.post--study #post {
   fill: #e42121;
}

.post--rest #post {
   fill: #19c520;
}
</style>
